import './index.css';
import Header from './components/Header.js';
import Main from './components/Main.js';
import PopupWithForm from './components/PopupWithForm.js';
import Footer from './components/Footer.js';

function App() {

  function handleEditAvatarClick() {
    const editAvatar = document.querySelector(".popup_type_avatar");
    editAvatar.classList.add("popup_opened");
  }

  function handleEditProfileClick() {
    const editProfile = document.querySelector(".popup_type_edit");
    editProfile.classList.add("popup_opened");
  }

  function handleAddPlaceClick() {
    const addPlace = document.querySelector(".popup_type_add");
    addPlace.classList.add("popup_opened");
  }

  return (
  <div className="root">
  <div className="root__container">
    <Header />
    <Main
      onEditAvatar={handleEditAvatarClick}
      onEditProdile={handleEditAvatarClick}
      onAddPlace={handleAddPlaceClick}
    />
    <PopupWithForm
      name="edit"
      title="Edit Profile"
      submitButton="Save"
    >
    <fieldset className="form__text">
      <input id="profile-name" className="form__item form__item_field_name" type="text" name="name" placeholder="Name" minLength={2} maxLength={40} required />
      <span id="profile-name-error" className="form__error" />
      <input id="profile-text" className="form__item form__item_field_description" type="text" name="about" placeholder="About me" minLength={2} maxLength={40} required />
      <span id="profile-text-error" className="form__error" />
    </fieldset>





    </PopupWithForm>
    <PopupWithForm
      name="add"
      title="New Place"
      submitButton=""
    >
    </PopupWithForm>
    <PopupWithForm
      name="avatar"
      title="Change profile picture"
      submitButton=""
    >
    </PopupWithForm>
    <PopupWithForm
      name="delete"
      title="Are you sure?"
      submitButton=""
    >
    </PopupWithForm>
    <Footer />
  </div>

  {/*creating a new element*/}
  <template className="element-template" id="element" />
</div>

  );
}

export default App;
