import React from 'react';
import editButton from '../images/edit.svg';
import {api} from '../utils/Api.js';

function Main(props) {

  const [userName, setUserName] = React.useState("");
  const [userDescription, setUserDescription] = React.useState("");
  const [userAvatar, setUserAvatar] = React.useState("");
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    function gatherUserInfo() {
      return fetch(this._baseUrl + "/users/me", {
        headers: this._headers
      })
      .then(res => res.ok ? res.json() : Promise.reject(new Error(res.statusText)))
    }

    function getInitialCards() {
      return fetch(this._baseUrl + "/cards", {
          headers: this._headers
      })
      .then(res => res.ok ? res.json() : Promise.reject(new Error(res.statusText)))
    }

    api.gatherUserInfo().then((result) => {
      setUserName(result.name);
      setUserDescription(result.about);
      setUserAvatar(result.avatar);
    })

    api.getInitialCards().then((items) => {
      setCards([items]);
    })

    cards.map(card => {
      return(
        <li className="element"> 
          <button className="element__delete" type="button" aria-label="delete"></button>
          <img className="element__photo" src={card.link} alt="unloadable image" /> 
          <div className="element__container">
            <h2 className="element__title">{card.name}</h2>
            <div className="element__likes">
              <button className="element__heart" type="button" aria-label="heart"></button>
              <p className="element__number">{card.like}</p>
           </div>
          </div>
        </li>
      )
    })


  });

  return (
    <main className="content">
      <section className="profile">
        <img className="profile__pic" src={userAvatar} alt="profile" onClick={props.onEditAvatar}/>
        <img className="profile__pic-edit" src={editButton} alt="edit button" />
        <div className="profile__info">
          <h1 className="profile__title">{userName}</h1>
          <button className="profile__edit" type="button" aria-label="edit" onClick={props.onEditProfile} />
          <p className="profile__subtitle">{userDescription}</p>
        </div>
        <button className="profile__add-button" type="button" aria-label="add" onClick={props.onAddPlace} />
      </section>
      <section class="elements">
        <ul class="elements__list">
          {cards}
        </ul>
      </section>
    </main>
  )
}

export default Main;
