import editButton from '../images/edit.svg'

function Main() {

  function handleEditAvatarClick() {
      const edit = document.querySelector(".profile__pic-edit");
      edit.classList.add(".popup_opened");
  }

  return (
     <main className="content">
        <section className="profile">
          <img className="profile__pic" src="data:," alt="profile" />
          <img className="profile__pic-edit" src={editButton} alt="edit button" onClick={handleEditAvatarClick} />
          <div className="profile__info">
            <h1 className="profile__title">Temp</h1>
            <button className="profile__edit" type="button" aria-label="edit" />
            <p className="profile__subtitle" />
          </div>
          <button className="profile__add-button" type="button" aria-label="add" />
        </section>
        <section className="elements">
          <ul className="elements__list">
          </ul>
        </section>
      </main>
    )
}

export default Main;
